create database if not exists ims;
create table if not exists ims.customers(id int primary key auto_increment, first_name varchar(40), surname varchar(40), address varchar(100), email varchar(100), mobile varchar(11));
create table if not exists ims.items(id int primary key auto_increment, item_name varchar(100), price double, stock int);
create table if not exists ims.orders(id int primary key auto_increment, customer_id int, item_id int not null, date date, foreign key(customer_id) references customers(id), foreign key(item_id) references items(id));
create table if not exists ims.orderline(id int primary key auto_increment, order_id int, customer_id int, item_id int, quantity int, price double, total double, foreign key(order_id) references orders(id), foreign key(customer_id) references customers(id), foreign key(item_id) references items(id));